valid_ga   = /\d+\/\d+\/\d+/;
parse_name = /(\w)(\d\d\d)\.*([\w\d]*)\.*([~=\?#!]*)/;

ga_catalog = {};


msg.payload.map( function(item) {
    var ga = item["col2"]

    if (valid_ga.exec(ga)) {

        name    = item["col1"];
        dpt     = item["col6"];

        match = parse_name.exec(name) 

        if ( match ) {

            trade   = match[1];
            room    = match[2];
            subname = match[3];
            dimming = /~/.test(match[4]);
            value   = /=/.test(match[4]);
            szene   = /#/.test(match[4]);
            status  = /\?/.test(match[4]);
            reply   = /!/.test(match[4]);

            if (typeof dpt === "undefined" || dpt === null) {
                guess_dpt = (function() {
                    switch (trade) {
                        case "L":
                            if (dimming) { 
                                return "3-7";
                            } else if (value || reply) {
                                return "5-1";
                            } else if (szene) {
                                return "18-1";
                            } else {
                                return "1-1";
                            }
                            break;
                        case "W":
                            return "1-1";
                        case "T":
                            return "9-1"
                        case "H":
                            return "9-7"
                        default: 
                            return false;
                    }
                })();
                if (guess_dpt) {
                    dpt = "DPST-" + guess_dpt;
                }

            }
        }


        match = parse_name.exec(name);

        if (match) {
            ga_catalog[ga] = {  trade:      trade,
                                room:       room,
                                subname:    subname,
                                name:       name,
                                dpt:        dpt,
                                dimming:    dimming,
                                value:      value,
                                szene:      szene,
                                status:     status,
                                reply:      reply
            };
        }
    }

})

newMsg = {};
newMsg.payload = {
        ga_catalog: ga_catalog
    }
return newMsg;

